<?phpclass about extends Controller {    function __construct() {        parent::__construct();    }    public function index() {        $ab = $this->model->loadabout('aboutus');        if ($ab) {            $abus = $ab->fetch();            $this->data['[VARABOUT]'] = htmlspecialchars( $abus['aboutus']);//            $this->data['[VARABOUT]'] = htmlspecialchars(str_replace(PHP_EOL, '<br>', $abus['aboutus']));        }        $this->view->render('about/index', $this->data);    }}